public class CacheUtil0<K, V> implements Computable<K, V> {

    Map<K, V> map = new HashMap<K, V>();

    private Computable computable;

    public Computable getComputable() {
        return computable;
    }

    public void setComputable(Computable computable) {
        this.computable = computable;
    }

    /**
     * ???????????????hashMap??????????????????synchronized4?????????
     * ????????????????????????4?????????????????????????????????
     * ???compute??????????????????compute????????????????????????
     *
     * @param arg
     * @return
     * @throws InterruptedException
     */
    public synchronized V compute(K arg) throws InterruptedException {

        V value = map.get(arg);
        if (value == null) {
            Object newValue = computable.compute(arg);
            value = (V) newValue;
            map.put(arg, value);
        }
        return value;
    }
}
